    int tail;
    std::vector<Message> data;
};

std::mutex mtx;

// 初始化消息队列
void init(MessageQueue &queue, int size)
{
    std::lock_guardstd::mutex lock(mtx);
    queue.size = size;
    queue.head = 0;
    queue.tail = 0;
    queue.data.resize(size);
}

