    destroy(queue);

    return 0;
}

#include <iostream>
#include <thread>
#include <vector>
#include <queue>
#include <mutex>
#include <condition_variable>
#include <atomic>
#include <future>

class ThreadPool
{
public:
    ThreadPool(size_t num_threads)
        : stop_(false)
    {
        for (size_t i = 0; i < num_threads; ++i)
        {
            threads_.emplace_back([this]()
            {
                while (true)
                {
                    std::function<void()> task;
                    {
                        std::unique_lock<std::mutex> lock(mutex_);
                        cond_.wait(lock, [this]{ return stop_ || !tasks_.empty(); });
                        if (stop_ && tasks_.empty())
                            return;
                        task = std::move(tasks_.front());
                        tasks_.pop();
                    }
                    task();
                }
            });
        }
    }

    ~ThreadPool()
    {
        {
            std::unique_lock<std::mutex> lock(mutex_);
            stop_ = true;
        }
        cond_.notify_all();
        for (auto& thread : threads_)
            thread.join();
    }

    template <class T>
    auto AddTask(T task) -> std::future<decltype(task())>
    {
        auto wrapper = std::make_shared<std::packaged_task<decltype(task())()>>(std::move(task));
        {
            std::unique_lock<std::mutex> lock(mutex_);
            tasks_.emplace([=]{ (*wrapper)(); });
        }
        cond_.notify_one();
        return wrapper->get_future();
    }

    void Wait()
    {
        std::unique_lock<std::mutex> lock(mutex_);
        cond_.wait(lock, [this]{ return tasks_.empty(); });
    }

    size_t Size()
    {
        std::unique_lock<std::mutex> lock(mutex_);
        return tasks_.size();
    }

private:
    std::vector<std::thread> threads_;
    std::queue<std::function<void()>> tasks_;
    std::mutex mutex_;
    std::condition_variable cond_;
    std::atomic<bool> stop_;
};

// 创建一个线程池，包含4个线程
ThreadPool pool(4);

// 添加一个任务，该任务是一个普通的函数
auto task1 = pool.AddTask([]()
{
    std::cout << "Hello from a worker thread!" << std::endl;
    return 42;
});

// 添加一个任务，该任务是一个 Lambda 表达式
auto task2 = pool.AddTask([]()
{
    std::cout << "Hello from another worker thread!" << std::endl;
    return 123;
});

// 等待所有任务完成
pool.Wait();

// 获取任务的返回值
std::cout << "Task1 returned " << task1.get() << std::endl;
std::cout << "Task2 returned " << task2.get() << std::endl;

// 获取当前任务队列的长度
std::cout << "Task queue size: " << pool.Size() << std::endl;

